import React, { useEffect, useRef } from 'react';

const CertificatePreview = ({ templateFile, previewName, xCoord, endXCoord, yCoord, fontSize }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!templateFile) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      // Set canvas dimensions
      canvas.width = img.width;
      canvas.height = img.height;
      
      // Draw background
      ctx.drawImage(img, 0, 0);
      
      // Text settings
      ctx.font = `${fontSize * 10}px Arial`;
      const text = previewName.trim();
      const metrics = ctx.measureText(text);
      const textWidth = metrics.width;
      const textHeight = fontSize * 15;

      // Calculate center position
      const center_x = xCoord + ((endXCoord - xCoord) - textWidth) / 2;

      // Draw guidelines and box
      ctx.fillStyle = 'rgba(0, 200, 0, 0.2)';
      ctx.strokeStyle = 'rgba(0, 200, 0, 0.8)';
      ctx.lineWidth = 2;
      
      // Draw text area box
      ctx.fillRect(xCoord, yCoord - textHeight, endXCoord - xCoord, textHeight + 20);
      ctx.strokeRect(xCoord, yCoord - textHeight, endXCoord - xCoord, textHeight + 20);
      
      // Draw center line
      ctx.beginPath();
      ctx.setLineDash([5, 5]);
      ctx.moveTo((xCoord + endXCoord) / 2, yCoord - textHeight - 10);
      ctx.lineTo((xCoord + endXCoord) / 2, yCoord + 20);
      ctx.stroke();

      // Draw text
      ctx.fillStyle = 'black';
      ctx.setLineDash([]);
      ctx.fillText(text, center_x, yCoord);
    };

    img.src = URL.createObjectURL(templateFile);
    return () => URL.revokeObjectURL(img.src);
  }, [templateFile, previewName, xCoord, endXCoord, yCoord, fontSize]);

  return <canvas ref={canvasRef} />;
};

export default CertificatePreview;
