import React, { useRef, useEffect } from 'react';

const CertificatePreview = ({ templateFile, previewName, xCoord, yCoord, fontSize }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!templateFile) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const img = new Image();
      img.src = e.target.result;
      img.onload = () => {
        const canvas = canvasRef.current;
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext('2d');
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0);
        // Use the provided font size (scaled) for preview text; font family fixed to Arial
        ctx.font = `${fontSize * 10}px Arial`;
        ctx.fillStyle = 'black';
        ctx.fillText(previewName, xCoord, yCoord);
      };
    };
    reader.readAsDataURL(templateFile);
  }, [templateFile, previewName, xCoord, yCoord, fontSize]);

  return (
    <div>
      <canvas ref={canvasRef} />
    </div>
  );
};

export default CertificatePreview;
