import React, { useRef, useEffect } from 'react';

const CertificatePreview = ({ templateFile, previewName, xCoord, endXCoord, yCoord, fontSize }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!templateFile) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      const img = new Image();
      img.src = e.target.result;
      img.onload = () => {
        const canvas = canvasRef.current;
        canvas.width = img.width;
        canvas.height = img.height;
        const ctx = canvas.getContext('2d');
        
        // Draw image
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(img, 0, 0);
        
        // Text settings
        ctx.font = `${fontSize * 10}px Arial`;
        const text = previewName.trim();
        const metrics = ctx.measureText(text);
        const textWidth = metrics.width;
        
        // Calculate center position
        const center_x = xCoord + ((endXCoord - xCoord) - textWidth) / 2;
        
        // Draw guidelines for visualization
        ctx.fillStyle = 'rgba(0, 200, 0, 0.1)';
        ctx.fillRect(xCoord, yCoord - fontSize * 12, endXCoord - xCoord, fontSize * 15);
        
        // Draw center line
        ctx.strokeStyle = 'rgba(0, 200, 0, 0.5)';
        ctx.setLineDash([5, 5]);
        ctx.beginPath();
        ctx.moveTo(xCoord + (endXCoord - xCoord) / 2, yCoord - fontSize * 12);
        ctx.lineTo(xCoord + (endXCoord - xCoord) / 2, yCoord + fontSize * 3);
        ctx.stroke();
        
        // Draw text
        ctx.fillStyle = 'black';
        ctx.setLineDash([]);
        ctx.fillText(text, center_x, yCoord);
      };
    };
    reader.readAsDataURL(templateFile);
  }, [templateFile, previewName, xCoord, endXCoord, yCoord, fontSize]);

  return <canvas ref={canvasRef} />;
};

export default CertificatePreview;
