import React, { useState, useEffect, useCallback } from 'react';

const CertificatePreview = ({ templateFile, textFields, showGuides = true }) => {
  const [previewUrl, setPreviewUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const generatePreview = useCallback(async () => {
    if (!templateFile) {
      setPreviewUrl(null);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('templateFile', templateFile);
      formData.append('textFields', JSON.stringify(textFields));

      const response = await fetch('http://localhost:5000/api/previewCertificate', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to generate preview');
      }

      // Cleanup old URL before creating new one
      if (previewUrl) {
        URL.revokeObjectURL(previewUrl);
      }

      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      setPreviewUrl(url);
    } catch (err) {
      console.error('Preview error:', err);
      setError('Failed to generate preview');
    } finally {
      setLoading(false);
    }
  }, [templateFile, textFields]);

  useEffect(() => {
    if (!templateFile || !textFields || !textFields.length) return;
    
    generatePreview();
    return () => {
      if (previewUrl) {
        URL.revokeObjectURL(previewUrl);
      }
    };
  }, [templateFile, textFields, generatePreview]);

  if (loading) {
    return (
      <div className="preview-loading">
        <div className="loading-spinner"></div>
        <p>Generating preview...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="preview-error">
        <p>{error}</p>
        <button onClick={generatePreview} className="retry-button">
          Retry
        </button>
      </div>
    );
  }

  if (!templateFile) {
    return (
      <div className="preview-placeholder">
        Upload a template to see preview
      </div>
    );
  }

  return (
    <div className="certificate-preview">
      <img 
        src={previewUrl} 
        alt="Certificate Preview" 
        style={{ 
          maxWidth: '100%', 
          height: 'auto',
          display: 'block',
          margin: '0 auto'
        }}
        onError={() => setError('Failed to load preview')}
      />
    </div>
  );
};

export default CertificatePreview;
