import React, { useState, useEffect } from 'react';

const CertificatePreview = ({ 
    templateFile,
    textFields,
    fontSize,
    font,
    textColor
}) => {
    const [previewUrl, setPreviewUrl] = useState(null);

    useEffect(() => {
        const generatePreview = async () => {
            if (!templateFile) return;

            try {
                const formData = new FormData();
                formData.append('templateFile', templateFile);
                formData.append('textFields', JSON.stringify(textFields));

                const response = await fetch('http://localhost:5000/api/previewCertificate', {
                    method: 'POST',
                    body: formData,
                });

                if (!response.ok) {
                    throw new Error('Failed to generate preview');
                }

                const blob = await response.blob();
                const url = URL.createObjectURL(blob);
                setPreviewUrl(url);

            } catch (error) {
                console.error('Preview generation failed:', error);
            }
        };

        generatePreview();

        // Cleanup function to prevent memory leaks
        return () => {
            if (previewUrl) {
                URL.revokeObjectURL(previewUrl);
            }
        };
    }, [templateFile, textFields, fontSize, font, textColor]); // Removed previewUrl from dependencies

    return (
        <div style={{
            width: '100%',
            minHeight: '400px',
            border: '2px dashed #ccc',
            borderRadius: '8px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            padding: '20px',
            backgroundColor: '#f8f9fa'
        }}>
            {!previewUrl ? (
                <div style={{
                    color: '#666',
                    textAlign: 'center'
                }}>
                    <p>Upload a template to see preview</p>
                </div>
            ) : (
                <img 
                    src={previewUrl} 
                    alt="Certificate Preview" 
                    style={{
                        maxWidth: '100%',
                        maxHeight: '100%',
                        objectFit: 'contain'
                    }}
                />
            )}
        </div>
    );
};

export default CertificatePreview;
