import React, { useEffect, useRef } from 'react';

const CertificatePreview = ({ templateFile, previewName, xCoord, yCoord, fontSize }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!templateFile) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const image = new Image();
    image.src = URL.createObjectURL(templateFile);
    image.onload = () => {
      canvas.width = image.width;
      canvas.height = image.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(image, 0, 0);
      ctx.font = `${fontSize * 10}px Arial`;
      ctx.fillStyle = 'black';
      // Center the preview name on the canvas by default
      const textWidth = ctx.measureText(previewName).width;
      const textX = (canvas.width - textWidth) / 2;
      const textY = canvas.height / 2;
      ctx.fillText(previewName, textX, textY);
    };
  }, [templateFile, previewName, fontSize]);

  return (
    <div style={{
      position: 'absolute',
      left: xCoord,
      top: yCoord,
      transform: 'translate(-50%, -50%)',
      boxShadow: '0 2px 8px rgba(0,0,0,0.3)'
    }}>
      <canvas ref={canvasRef} />
    </div>
  );
};

export default CertificatePreview;
