import React, { useState } from 'react';
import CertificatePreview from './CertificatePreview';

const EmailForm = () => {
  const [senderEmail, setSenderEmail] = useState('');
  const [appPassword, setAppPassword] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [xCoord, setXCoord] = useState(1000);
  const [yCoord, setYCoord] = useState(700);
  const [previewName, setPreviewName] = useState('Your Name');
  const [excelFile, setExcelFile] = useState(null);
  const [templateFile, setTemplateFile] = useState(null);
  const [font, setFont] = useState("FONT_HERSHEY_COMPLEX");
  const [fontSize, setFontSize] = useState(3);
  const [endXCoord, setEndXCoord] = useState(1200);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formData = new FormData();
    formData.append('senderEmail', senderEmail);
    formData.append('appPassword', appPassword);
    formData.append('subject', subject);
    formData.append('body', body);
    formData.append('xCoord', xCoord);
    formData.append('yCoord', yCoord);
    formData.append('font', font);
    formData.append('fontSize', fontSize);
    if (excelFile) {
      formData.append('excelFile', excelFile);
    }
    if (templateFile) {
      formData.append('templateFile', templateFile);
    }
    
    try {
      const response = await fetch('http://localhost:5000/api/sendCertificates', {
        method: 'POST',
        body: formData,
      });
      if (response.ok) {
        alert("Emails sent successfully!");
      } else {
        const errorData = await response.json();
        alert("Error sending emails: " + errorData.error);
      }
    } catch (error) {
      alert("Error: " + error.message);
    }
  };

  return (
    <div className="form-preview-wrapper">
      <form onSubmit={handleSubmit} className="form-fields">
        <div className="form-grid">
          {/* Email Configuration Section */}
          <div className="form-section">
            <h3>Email Configuration</h3>
            <p className="section-desc">Configure your email settings</p>
            <div className="input-group">
              <label>Sender Email:</label>
              <input
                type="email"
                value={senderEmail}
                onChange={(e) => setSenderEmail(e.target.value)}
                placeholder="your-email@example.com"
                required
              />
              <small>Use your Gmail address</small>
            </div>
            <div className="input-group">
              <label>App Password:</label>
              <input
                type="password"
                value={appPassword}
                onChange={(e) => setAppPassword(e.target.value)}
                required
              />
            </div>
          </div>

          {/* Certificate Setup Section */}
          <div className="form-section">
            <h3>Certificate Setup</h3>
            <p className="section-desc">Upload your data and template</p>
            <div className="input-group">
              <label>Excel File (xlsx):</label>
              <input
                type="file"
                accept=".xlsx"
                onChange={(e) => setExcelFile(e.target.files[0])}
                required
              />
            </div>
            <div className="input-group">
              <label>Certificate Template:</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setTemplateFile(e.target.files[0])}
                required
              />
            </div>
          </div>
        </div>

        {/* Email Content Section */}
        <div className="form-section">
          <h3>Email Content</h3>
          <p className="section-desc">Customize your email message</p>
          <div className="input-group">
            <label>Subject:</label>
            <input
              type="text"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label>Body:</label>
            <textarea
              value={body}
              onChange={(e) => setBody(e.target.value)}
              required
              rows="4"
            />
          </div>
        </div>

        {/* Coordinate and Preview Section */}
        <div className="coordinate-preview-section">
          <div className="coordinate-controls">
            <h3>Text Positioning</h3>
            <p className="section-desc">Adjust name placement and style</p>
            
            <div className="coordinate-inputs">
              <div className="input-group">
                <label>X Coordinate:</label>
                <input
                  type="number"
                  value={xCoord}
                  onChange={(e) => setXCoord(Number(e.target.value))}
                  required
                />
              </div>
              <div className="input-group">
                <label>Y Coordinate:</label>
                <input
                  type="number"
                  value={yCoord}
                  onChange={(e) => setYCoord(Number(e.target.value))}
                  required
                />
              </div>
            </div>

            <div className="font-controls">
              <div className="input-group">
                <label>Font:</label>
                <select value={font} onChange={(e) => setFont(e.target.value)}>
                  <option value="FONT_HERSHEY_SIMPLEX">FONT_HERSHEY_SIMPLEX</option>
                  <option value="FONT_HERSHEY_PLAIN">FONT_HERSHEY_PLAIN</option>
                  <option value="FONT_HERSHEY_DUPLEX">FONT_HERSHEY_DUPLEX</option>
                  <option value="FONT_HERSHEY_COMPLEX">FONT_HERSHEY_COMPLEX</option>
                  <option value="FONT_HERSHEY_TRIPLEX">FONT_HERSHEY_TRIPLEX</option>
                  <option value="FONT_HERSHEY_COMPLEX_SMALL">FONT_HERSHEY_COMPLEX_SMALL</option>
                  <option value="FONT_HERSHEY_SCRIPT_SIMPLEX">FONT_HERSHEY_SCRIPT_SIMPLEX</option>
                  <option value="FONT_HERSHEY_SCRIPT_COMPLEX">FONT_HERSHEY_SCRIPT_COMPLEX</option>
                  <option value="FONT_ITALIC">FONT_ITALIC</option>
                </select>
              </div>
              <div className="input-group">
                <label>Font Size:</label>
                <input
                  type="number"
                  value={fontSize}
                  onChange={(e) => setFontSize(Number(e.target.value))}
                  required
                />
              </div>
              <div className="input-group">
                <label>Preview Name:</label>
                <input
                  type="text"
                  value={previewName}
                  onChange={(e) => setPreviewName(e.target.value)}
                />
              </div>
            </div>
          </div>

          <div className="preview-container">
            <h3>Certificate Preview</h3>
            {templateFile ? (
              <CertificatePreview
                templateFile={templateFile}
                previewName={previewName}
                xCoord={xCoord}
                yCoord={yCoord}
                fontSize={fontSize}
              />
            ) : (
              <p>Upload a template to see preview</p>
            )}
          </div>
        </div>

        <button type="submit" className="submit-button">
          Generate & Send Certificates
        </button>
      </form>
    </div>
  );
};

export default EmailForm;
