import React, { useEffect, useRef } from 'react';

const CertificatePreview = ({ templateFile, previewName, xCoord, endXCoord, yCoord, fontSize, showGuides = true }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      // Set canvas size and draw image
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0);

      // Calculate text metrics and center position
      ctx.font = `${fontSize * 10}px Arial`;
      const textMetrics = ctx.measureText(previewName);
      const textWidth = textMetrics.width;
      const textHeight = fontSize * 15;

      // Calculate center position
      const centerX = xCoord + ((endXCoord - xCoord) - textWidth) / 2;
      
      if (showGuides) {
        // Draw green box (preview only)
        ctx.fillStyle = 'rgba(0, 200, 0, 0.2)';
        ctx.strokeStyle = 'rgba(0, 200, 0, 0.8)';
        ctx.lineWidth = 2;
        ctx.fillRect(xCoord, yCoord - textHeight, endXCoord - xCoord, textHeight + 20);
        ctx.strokeRect(xCoord, yCoord - textHeight, endXCoord - xCoord, textHeight + 20);
      }
      
      // Draw text (centered)
      ctx.fillStyle = 'black';
      ctx.textBaseline = 'bottom';
      const x_pos = Math.max(xCoord, Math.min(centerX, endXCoord - textWidth));
      ctx.fillText(previewName, x_pos, yCoord);
    };

    img.src = URL.createObjectURL(templateFile);
    return () => URL.revokeObjectURL(img.src);
  }, [templateFile, previewName, xCoord, endXCoord, yCoord, fontSize, showGuides]);

  return <canvas ref={canvasRef} className="certificate-preview" />;
};

export default CertificatePreview;
