import React, { useState } from 'react';
import CertificatePreview from './CertificatePreview';

const EmailForm = () => {
  const [senderEmail, setSenderEmail] = useState('');
  const [appPassword, setAppPassword] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [xCoord, setXCoord] = useState(1000);
  const [yCoord, setYCoord] = useState(700);
  const [previewName, setPreviewName] = useState('Your Name');
  const [excelFile, setExcelFile] = useState(null);
  const [templateFile, setTemplateFile] = useState(null);
  const [font, setFont] = useState("FONT_HERSHEY_COMPLEX");
  const [fontSize, setFontSize] = useState(3);
  const [endXCoord, setEndXCoord] = useState(1200);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [previewData, setPreviewData] = useState(null);
  const [error, setError] = useState(null);
  const [sendingProgress, setSendingProgress] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);

  const handleError = (error) => {
    let userMessage = "";
    
    if (typeof error === 'string') {
      if (error.includes("535-5.7.8")) {
        userMessage = "Wrong password! Please make sure you're using an App Password from your Google Account settings, not your regular Gmail password.";
      } else if (error.includes("535")) {
        userMessage = "Login failed! Please check your email and App Password. Remember to use an App Password, not your regular Gmail password.";
      } else if (error.includes("certificate")) {
        userMessage = "Certificate generation failed! Please check your template image and text positioning.";
      } else if (error.includes("columns")) {
        userMessage = "Excel file format error! Your file must have columns named exactly 'name' and 'email'.";
      } else if (error.includes("SMTP")) {
        userMessage = "Email server connection failed. Please check your internet connection and try again.";
      } else {
        userMessage = error;
      }
    } else {
      userMessage = "An unexpected error occurred. Please try again.";
    }
    
    setError(userMessage);
    // Scroll to error message
    setTimeout(() => {
      const errorElement = document.querySelector('.error-message');
      if (errorElement) {
        errorElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }, 100);

    // Auto dismiss after 15 seconds for password errors, 8 seconds for others
    const timeout = userMessage.includes("password") ? 15000 : 8000;
    setTimeout(() => setError(null), timeout);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setPreviewData({
      senderEmail,
      subject,
      body,        // Add body to previewData
      templateFile,
      previewName,
      xCoord,
      endXCoord,
      yCoord,
      fontSize,
    });
    setShowConfirmation(true);

    // Scroll to confirmation modal
    setTimeout(() => {
      const modalElement = document.querySelector('.confirmation-content');
      if (modalElement) {
        modalElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }, 100);
  };

  const handleConfirmedSubmit = async () => {
    setError(null);
    setSuccessMessage(null);
    setSendingProgress({
      total: 0,
      current: 0,
      completed: 0,
      failed: 0,
      currentEmail: 'Connecting...',
      failedEmails: []
    });

    // Set up SSE before sending request
    const eventSource = new EventSource('http://localhost:5000/api/progress');
    
    eventSource.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setSendingProgress(data);
    };

    eventSource.onerror = () => {
      eventSource.close();
    };

    try {
      const formData = new FormData();
      formData.append('senderEmail', senderEmail);
      formData.append('appPassword', appPassword);
      formData.append('subject', subject);
      formData.append('body', body);
      formData.append('xCoord', xCoord);
      formData.append('yCoord', yCoord);
      formData.append('font', font);
      formData.append('fontSize', fontSize);
      formData.append('endXCoord', endXCoord);
      if (excelFile) {
        formData.append('excelFile', excelFile);
      }
      if (templateFile) {
        formData.append('templateFile', templateFile);
      }

      const response = await fetch('http://localhost:5000/api/sendCertificates', {
        method: 'POST',
        body: formData,
      });
      
      const data = await response.json();
      
      if (response.ok) {
        if (data.failed_details.length > 0) {
          const failedEmails = data.failed_details.map(f => f[0]).join(", ");
          setError(`Successfully sent ${data.successful} emails, but failed to send to: ${failedEmails}`);
        } else {
          setSuccessMessage(`Successfully sent ${data.successful} certificates!`);
        }
      } else {
        handleError(data.error);
      }
    } catch (error) {
      handleError(error.message);
    } finally {
      eventSource.close();
      setShowConfirmation(false);
      setSendingProgress(null);
    }
  };

  return (
    <div className="form-preview-wrapper">
      {error && (
        <div className="error-message">
          <div className="error-content">
            <span className="error-icon">⚠️</span>
            <p>{error}</p>
            <button onClick={() => setError(null)} className="error-close">×</button>
          </div>
        </div>
      )}
      {successMessage && (
        <div className="success-message">
          <div className="success-content">
            <span className="success-icon">✅</span>
            <p>{successMessage}</p>
            <button onClick={() => setSuccessMessage(null)} className="success-close">×</button>
          </div>
        </div>
      )}
      <form onSubmit={handleSubmit} className="form-fields">
        <div className="form-grid">
          {/* Email Configuration Section */}
          <div className="form-section">
            <h3>Email Configuration</h3>
            <p className="section-desc">Configure your email settings</p>
            <div className="input-group">
              <label>Sender Email:</label>
              <input
                type="email"
                value={senderEmail}
                onChange={(e) => setSenderEmail(e.target.value)}
                placeholder="your-email@example.com"
                required
              />
              <small>Use your Gmail address</small>
            </div>
            <div className="input-group">
              <label>App Password:</label>
              <input
                type="password"
                value={appPassword}
                onChange={(e) => setAppPassword(e.target.value)}
                required
              />
            </div>
          </div>

          {/* Certificate Setup Section */}
          <div className="form-section">
            <h3>Certificate Setup</h3>
            <p className="section-desc">Upload your data and template</p>
            <div className="input-group">
              <label>Excel File (xlsx):</label>
              <input
                type="file"
                accept=".xlsx"
                onChange={(e) => setExcelFile(e.target.files[0])}
                required
              />
            </div>
            <div className="input-group">
              <label>Certificate Template:</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setTemplateFile(e.target.files[0])}
                required
              />
            </div>
          </div>
        </div>

        {/* Email Content Section */}
        <div className="form-section">
          <h3>Email Content</h3>
          <p className="section-desc">Customize your email message</p>
          <div className="input-group">
            <label>Subject:</label>
            <input
              type="text"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label>Body:</label>
            <textarea
              value={body}
              onChange={(e) => setBody(e.target.value)}
              required
              rows="4"
            />
          </div>
        </div>

        {/* Coordinate and Preview Section */}
        <div className="coordinate-preview-section">
          <div className="coordinate-controls">
            <h3>Text Positioning</h3>
            <p className="section-desc">Adjust name placement and style</p>
            
            <div className="coordinate-inputs">
              <div className="input-group">
                <label>Start X:</label>
                <input
                  type="number"
                  value={xCoord}
                  onChange={(e) => setXCoord(Number(e.target.value))}
                  required
                />
              </div>
              <div className="input-group">
                <label>End X:</label>
                <input
                  type="number"
                  value={endXCoord}
                  onChange={(e) => setEndXCoord(Number(e.target.value))}
                  required
                />
              </div>
              <div className="input-group">
                <label>Y Coordinate:</label>
                <input
                  type="number"
                  value={yCoord}
                  onChange={(e) => setYCoord(Number(e.target.value))}
                  required
                />
              </div>
            </div>

            <div className="font-controls">
              <div className="input-group">
                <label>Font:</label>
                <select value={font} onChange={(e) => setFont(e.target.value)}>
                  <option value="FONT_HERSHEY_SIMPLEX">FONT_HERSHEY_SIMPLEX</option>
                  <option value="FONT_HERSHEY_PLAIN">FONT_HERSHEY_PLAIN</option>
                  <option value="FONT_HERSHEY_DUPLEX">FONT_HERSHEY_DUPLEX</option>
                  <option value="FONT_HERSHEY_COMPLEX">FONT_HERSHEY_COMPLEX</option>
                  <option value="FONT_HERSHEY_TRIPLEX">FONT_HERSHEY_TRIPLEX</option>
                  <option value="FONT_HERSHEY_COMPLEX_SMALL">FONT_HERSHEY_COMPLEX_SMALL</option>
                  <option value="FONT_HERSHEY_SCRIPT_SIMPLEX">FONT_HERSHEY_SCRIPT_SIMPLEX</option>
                  <option value="FONT_HERSHEY_SCRIPT_COMPLEX">FONT_HERSHEY_SCRIPT_COMPLEX</option>
                  <option value="FONT_ITALIC">FONT_ITALIC</option>
                </select>
              </div>
              <div className="input-group">
                <label>Font Size:</label>
                <input
                  type="number"
                  value={fontSize}
                  onChange={(e) => setFontSize(Number(e.target.value))}
                  required
                />
              </div>
              <div className="input-group">
                <label>Preview Name:</label>
                <input
                  type="text"
                  value={previewName}
                  onChange={(e) => setPreviewName(e.target.value)}
                />
              </div>
            </div>
          </div>

          <div className="preview-container">
            <h3>Certificate Preview</h3>
            {templateFile ? (
              <CertificatePreview
                templateFile={templateFile}
                previewName={previewName}
                xCoord={xCoord}
                endXCoord={endXCoord}
                yCoord={yCoord}
                fontSize={fontSize}
              />
            ) : (
              <p>Upload a template to see preview</p>
            )}
          </div>
        </div>

        <button type="submit" className="submit-button">
          Generate & Send Certificates
        </button>
      </form>

      {showConfirmation && (
        <div className="confirmation-modal">
          <div className="confirmation-content">
            {sendingProgress ? (
              <div className="sending-progress">
                <h2>Sending Certificates...</h2>
                <div className="progress-details">
                  <div className="progress-bar">
                    <div 
                      className="progress-fill" 
                      style={{ 
                        width: `${(sendingProgress.completed / sendingProgress.total) * 100}%` 
                      }}
                    />
                  </div>
                  <div className="progress-stats">
                    <p>Total Recipients: {sendingProgress.total}</p>
                    <p>Completed: {sendingProgress.completed}</p>
                    <p>Failed: {sendingProgress.failed}</p>
                    <p id>Current: {sendingProgress.currentEmail}</p>
                  </div>
                  {sendingProgress.failedEmails.length > 0 && (
                    <div className="failed-emails">
                      <h4>Failed Emails:</h4>
                      <ul>
                        {sendingProgress.failedEmails.map((email, index) => (
                          <li key={index}>{email}</li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
            ) : (
              <>
                <h2>Confirm Email Send</h2>
                <p>Are you sure you want to send certificates to all recipients?</p>
                
                <div className="confirmation-details">
                  <p><strong>From:</strong> {previewData.senderEmail}</p>
                  <p><strong>Subject:</strong> {previewData.subject}</p>
                  <div className="email-body-preview">
                    <strong>Email Body:</strong>
                    <pre>{previewData.body}</pre>
                  </div>
                </div>

                <div className="certificate-preview-container">
                  <h3>Certificate Preview</h3>
                  <CertificatePreview
                    templateFile={previewData.templateFile}
                    previewName={previewData.previewName}
                    xCoord={previewData.xCoord}
                    endXCoord={previewData.endXCoord}
                    yCoord={previewData.yCoord}
                    fontSize={previewData.fontSize}
                  />
                </div>

                <div className="confirmation-actions">
                  <button onClick={handleConfirmedSubmit} className="confirm-button">
                    Yes, Send Emails
                  </button>
                  <button onClick={() => setShowConfirmation(false)} className="cancel-button">
                    Cancel
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default EmailForm;
