import React, { useEffect, useRef } from 'react';

const CertificatePreview = ({ templateFile, previewName, xCoord, endXCoord, yCoord, fontSize }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      // Set canvas size to match image
      canvas.width = img.width;
      canvas.height = img.height;

      // Draw image
      ctx.drawImage(img, 0, 0);

      // Draw semi-transparent rectangle
      ctx.fillStyle = 'rgba(255, 165, 0, 0.2)'; // Orange with 0.2 opacity
      ctx.strokeStyle = 'rgba(255, 165, 0, 0.8)'; // Orange with 0.8 opacity
      ctx.lineWidth = 2;

      // Calculate text metrics
      ctx.font = `${fontSize * 10}px Arial`; // Approximate scaling
      const textMetrics = ctx.measureText(previewName);
      const textHeight = fontSize * 15; // Approximate height based on font size

      // Draw rectangle
      ctx.fillRect(xCoord, yCoord - textHeight, endXCoord - xCoord, textHeight + 20);
      ctx.strokeRect(xCoord, yCoord - textHeight, endXCoord - xCoord, textHeight + 20);

      // Draw text
      const textWidth = textMetrics.width;
      const centerX = xCoord + ((endXCoord - xCoord) - textWidth) / 2;
      
      ctx.fillStyle = 'black';
      ctx.fillText(previewName, centerX, yCoord);
    };

    img.src = URL.createObjectURL(templateFile);

    return () => {
      URL.revokeObjectURL(img.src);
    };
  }, [templateFile, previewName, xCoord, endXCoord, yCoord, fontSize]);

  return <canvas ref={canvasRef} className="certificate-preview" />;
};

export default CertificatePreview;
